import  { createContext,  } from 'react'
import moviedata from '../data/moviedata';
interface AppContextInterface {
    title: string;
    rang: number;
    year: number;
  }

export const MovieContext = createContext<AppContextInterface | null>(null);;
const sampleAppContext: AppContextInterface = {
    title: "Using React Context in a Typescript App",
    rang: 1,
    year: 1,
  };
export const MovieProvider= (props: { children: any; }) => {

  return (
    <MovieContext.Provider value={sampleAppContext}>
        {props.children}
    </MovieContext.Provider>
  )
}
